package com.zlp.sales;import com.zlp.utils.AppKeys;import android.app.AlertDialog;import android.content.Context;import android.content.DialogInterface;import android.os.Bundle;import android.support.v4.app.Fragment;import android.view.LayoutInflater;import android.view.View;import android.view.View.OnClickListener;import android.view.ViewGroup;import android.widget.Button;public class AboutFragment extends Fragment {	private Context mContext;	private View mainView;	private Button btn_logout;	@Override	public View onCreateView(LayoutInflater inflater, ViewGroup container,			Bundle savedInstanceState) {		mainView = inflater.inflate(R.layout.about_layout, null);		mContext = getActivity();		initViews();		return mainView;	}	private void initViews() {		btn_logout = (Button) mainView.findViewById(R.id.logout);		btn_logout.setOnClickListener(listener);	}	private OnClickListener listener = new OnClickListener() {		@Override		public void onClick(View v) {			switch (v.getId()) {			case R.id.logout:				new AlertDialog.Builder(mContext)						.setMessage("Are you sure to logoutï¼Ÿ")						.setTitle("Rifle Sales")						.setIcon(R.drawable.rifle)						.setPositiveButton("Quit",								new DialogInterface.OnClickListener() {									@Override									public void onClick(DialogInterface dialog,											int which) {										AppKeys.uname = "";										getActivity().finish();									}								})						.setNegativeButton("Cancel",								new DialogInterface.OnClickListener() {									@Override									public void onClick(DialogInterface dialog,											int which) {									}								}).create().show();				break;			default:				break;			}		}	};}