package com.zlp.sales;import android.content.Context;import android.os.Bundle;import android.support.v4.app.Fragment;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;public class CheckHistoryFragment extends Fragment {	private Context mContext;	private View mainView;	private TextView tvCheckHistory;	@Override	public View onCreateView(LayoutInflater inflater, ViewGroup container,			Bundle savedInstanceState) {		mainView = inflater.inflate(R.layout.check_history_layout, null);		mContext = getActivity();		initViews();		return mainView;	}	private void initViews() {		tvCheckHistory = (TextView) mainView				.findViewById(R.id.check_history_tv);	}}